<template>
  <div :id="'d'+uid" ref="imgBg" :data-background-image="dSrc" class="lozad img-box select-none bg-contain bg-no-repeat">
    <img v-if="!loaded"
         :id="'i'+uid"
         ref="img"
         :alt="dAlt"
         :data-src="dSrc"
         :src="defaultImgUrl"
         class="lozad invisible relative img">
  </div>
</template>

<script lang="ts">
import { defineComponent, onMounted, ref, watchEffect, nextTick } from 'vue';
import lozad from 'lozad'
import { getRandomSerial } from "@/utils/random";

export default defineComponent({
  name: 'LazyImg',
  props: {
    dSrc: {
      type: String,
      required: true,
      default: ''
    },
    dAlt: {
      type: String,
      required: false,
      default: ''
    },
    forceLoad: {
      type: Boolean,
      default: false,
      required: false
    },
    needDefaultImg: {
      type: Boolean,
      default: false,
      required: false
    },
    displayDelay: {
      type: Number,
      default: 300,
      required: false
    }
  },
  setup(props, { emit }) {
    const defaultImgUrl = (props.needDefaultImg && ref<string>('data:image/png;base64,') || '')
    const img = ref<HTMLImageElement>({} as HTMLImageElement)
    const imgBg = ref<HTMLDivElement>({} as HTMLDivElement)
    const loaded = ref(false)
    const uid = ref<string>(getRandomSerial())

    const observer = lozad(
        '.lozad', {
          loaded: async (element: HTMLImageElement) => {
            await new Promise(resolve => {
              element.onload = resolve
            })
            imgBg.value.classList.add('img-box__completed')
            await new Promise<void>(resolve => setTimeout(() => resolve(), props.displayDelay))
            emit('img-loaded')
            await nextTick()
            imgBg.value.style.height = element.height.toString() + 'px'
            loaded.value = true
          },
        }
    )

    onMounted(async () => {
      await nextTick()
      img.value.classList.add(uid.value)
      imgBg.value.classList.add(uid.value)
      await nextTick()
      observer.observe()
      forceLoadService()
    })

    const forceLoadService = () => {
      watchEffect(async () => {
        if (props['forceLoad']) {
          observer.triggerLoad(img.value)
        }
      })
    }

    return {
      defaultImgUrl,
      img,
      imgBg,
      loaded,
      uid
    }
  }
});
</script>

<style lang="scss" scoped>
.img-box {
  opacity: 0;
  transition: 1s ease;

  &__completed {
    opacity: 1;
  }
}
</style>
